$('[name*="[field_name]"]').change(function(){   
    var valueSplit=$(this).val();   
    if(jQuery.trim(valueSplit) != ""){
			valueSplit=valueSplit.split('::');
			valueSplit=(valueSplit.length > 1?valueSplit[1]:valueSplit[0]);        
			$(this).val(valueSplit);       
			$(this).closest('.cell-info-grid').find('.popup-autocomplete').val(valueSplit);   
		}
});

$('[name*="[maximum_allocation_time_field]"]').change(function(){
    var valueSplit=$(this).val();
    if(jQuery.trim(valueSplit) != ""){
        valueSplit=valueSplit.split('::');
        valueSplit=(valueSplit.length > 1?valueSplit[1]:valueSplit[0]);
        $(this).val(valueSplit);
        $(this).closest('.cell-info-grid').find('.popup-autocomplete').val(valueSplit);   
    }
});
